    "src": "/static/7cf7b0e58065df07846876333e55a965/083f8/resume-v1.png",
    "srcSet": ["/static/7cf7b0e58065df07846876333e55a965/ad4a5/resume-v1.png 205w", "/static/7cf7b0e58065df07846876333e55a965/74ab4/resume-v1.png 410w", "/static/7cf7b0e58065df07846876333e55a965/083f8/resume-v1.png 820w", "/static/7cf7b0e58065df07846876333e55a965/e717f/resume-v1.png 1132w"],
